import { IError } from "../types";
import { CountryResponse } from "../types/tools";
type Response = CountryResponse & IError;
/**
 * Get more information about the country by providing an name/code.
 *
 * &nbsp;
 *
 * ### Parameters
 * - `name` - Name of the country or country code
 *
 * &nbsp;
 *
 * ### Usage Example
 * ```js
 * const { tools } = require('osu-api-extended');
 *
 * function main() {
 *   try {
 *     const result = tools.country_details('US');
 *     // or
 *     const result = tools.country_details('United States');
 *     if (result.error != null) {
 *       console.log(result.error);
 *       return;
 *     };
 *
 *
 *     console.log(result);
 *   } catch (error) {
 *     console.log(error);
 *   };
 * };
 *
 * main();
 * ```
 */
export declare const country_details: (name: string) => Response;
export {};
